public class Foo {
  public void foo2() {
        if(a || b || c || d){
            // doSomething()
        }

        List<String> filterList = list.stream()
            .filer(x -> x==1 || x==2 || x==3 || x==4)
            .collect(Collectors.toList());

        // too many condition
        boolean result = a && b && !c && d;
    }
}
