public class Foo {
    public void bar() {
        ModelClass model = new ModelClass();
        model.setHasTicket(getStreamInfo().anyMatch(x -> Objects.equals("1", x)
            || Objects.equals("2", x)));
    }

    private Stream<String> getStreamInfo() {
        List<String> stringList = new ArrayList<>();
        stringList.add("1");
        return stringList.stream();
    }
}

